import React, { useState } from 'react'
import './navbar.css';
import { useContext } from "react";
import { Context } from "../Context/Context";
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

function Navbar() {

    const { dispatch,user } = useContext(Context);

    const handleLogout = () => {
        dispatch({ type: "LOGOUT" });
    }
    return (
        <>
            <div className="nav-cont">
                <div className="profile-cont">
                {
                    user?.image ? <img src={user?.image} alt="" style={{width:'45px',height:'45px',borderRadius:'50px'}} /> 
                    : <AccountCircleIcon style={{fontSize:'60px',color:' rgb(0, 110, 255)'}} />
                }
                    <h3>{user.firstName}</h3>
                </div>
                <h2>Welcome to E-commorce Website</h2>
                <div className="nav-logout-btn">
                    <button onClick={handleLogout} className='logout-btn' >
                        Log Out
                    </button>
                </div>
            </div>
        </>
    )
}

export default Navbar
