import React, { useEffect, useState } from 'react'
import './cart.css';
import Image from '../Image/iphone.jpg';
import Navbar from '../Navbar/Navbar';

function Cart() {

    const [data, setData] = useState([]);
    const Obj = JSON.parse(localStorage.getItem('cart'));
    // console.log(Obj)
    // console.log(data);
    useEffect(() => {
        setData(Obj.products)
    }, []);

    return (
        <>
            {/* <Navbar /> */}
            <div className='cart-cont' >
                <div className='cart-heading' >
                    <h2>Shopping Cart</h2>
                    <p>You have {Obj.totalProducts} items in Cart</p>
                </div>

                <hr />
                <div className='cart-item-cont' >
                    {
                        data.map((item, index) => {
                            return <div className='products' key={index} >
                                {/* <div className='cart-img' >
                                    <img src={Image} alt="" className='cart-img' />
                                </div> */}
                                <div className='item-title' style={{ lineHeight: '0px' }}>
                                    <h4>Title</h4>
                                    <p>{item.title}</p>
                                </div>
                                <div className='item-title' style={{ lineHeight: '0px' }}>
                                    <h4>Brand</h4>
                                    <p>{item.title}</p>
                                </div>
                                <div style={{ lineHeight: '0px' }}>
                                    <h4>Quantity</h4>
                                    <p>{item.quantity}</p>
                                </div>
                                <div className='price' style={{ lineHeight: '0px' }}>
                                    <h4>Price</h4>
                                    <p>{item.price}</p>
                                </div>
                            </div>
                        })
                    }
                </div>
            </div>
        </>
    )
}

export default Cart
