import { createContext, useReducer, useEffect } from "react"
import Reducer from "./reducer";

const INTIAL_STATE = {
    user:JSON.parse(localStorage.getItem("user")) || null,
    isLoading:false,
    error:false
}

const INTIAL_COUNT = {
    count:0
}


export const Context = createContext(INTIAL_STATE);

export const Count_Context = createContext(INTIAL_COUNT);

export const CountProvider = ({children}) => {
    const [state,dispatch] = useReducer(Reducer,INTIAL_COUNT);

    return(
        <Context.Provider
            value={{
                count:state.count,
                dispatch
            }}
        >
        {children}
        </Context.Provider>
    )
}


export const ContextProvider = ({children}) => {
    const [state,dispatch] = useReducer(Reducer,INTIAL_STATE);

    useEffect(() => {
        localStorage.setItem("user", JSON.stringify(state.user))
    }, [state.user])

    return (
        <Context.Provider
            value={{
                user:state.user,
                isLoading:state.isLoading,
                error:state.error,
                dispatch
            }}
            >
            {children}
        </Context.Provider>
    )
}
