import {useState}from 'react';
import './card.css';
import CartComp from '../CartComp';

function Card({ item }) {
    //console.log(item);
    //console.log(item.images);
    const Images = item.images;
    const [cartCount,setCartCount] = useState(0);
    const [amount,setAmount] = useState(0);

    const handleCartCount = () => {
        setCartCount(cartCount + 1);
        setAmount(amount + item.price)
        // const cart = JSON.parse(localStorage.getItem('cart'));
    }

    return (
        <>
            <div className="card-cont">
                <img src={Images[0]} alt="" className='card-pro-img' />
                <div className="card-info">
                    <div className='card-brand' >
                        <h3>{item.title}</h3>
                        <h4>{item.brand}</h4>
                    </div>
                    <p style={{ textTransform: 'capitalize' }} >{item.category}</p>
                    <p>{item.description}</p>
                </div>
                <div style={{ border: '0.8px solid lightGray' }}>
                </div>
                <div className='card-prices' >
                    <div className='price-cont'>
                        {/* <h4>{Math.round(item.price - (item.price * item.discountPercentage / 100))}Rs</h4> */}
                        <h4>{item.price}Rs</h4>
                        <p>Discount: {item.discountPercentage}%</p>
                    </div>
                    <p>Rating: {item.rating}</p>
                </div>
                <button className='card-btn' onClick={handleCartCount} >Add to Cart</button>
            </div>

            {
                true && <CartComp cartCount={cartCount} amount={amount} />
            }
        </>
    )
}

export default Card
