import { useContext, useEffect, useState } from "react";
import Navbar from "../Navbar/Navbar";
import Card from "../Card/Card";
import './home.css';
import SearchIcon from '@mui/icons-material/Search';
import { Context } from "../Context/Context";

function Home() {

    const [products, setProducts] = useState([]);
    const [searchItem, setSearchItem] = useState('');
    const [min, setMin] = useState('');
    const [max, setMax] = useState('');
    const [result, setResult] = useState([]);
    const [count,setCount] = useState(0);
    const [amount,setAmount] = useState(0);
    
    
    // const {dispatch,count} = useContext(Context);

    useEffect(() => {
        fetch('https://dummyjson.com/products')
            .then(res => res.json())
            .then(data => setProducts(data.products));
        // console.log(products)
    }, [])


    const handleSearch = () => {
        fetch(`https://dummyjson.com/products/search?q=${searchItem}`)
            .then(res => res.json())
            .then(data => setProducts(data.products));
    }

    const handlePriceFilter = () => {
        fetch(`https://dummyjson.com/products`)
            .then(res => res.json())
            .then(data => setProducts(data.products));

        let newArray = [];
        for (let i = 0; i < products.length; i++) {
            if (products[i].price > min && products[i].price < max) {
                newArray.push(products[i]);
            }
        }
        return setResult(newArray);
    }
    //console.log(result);

    const handleCartCount = (price,id) => {
        setCount(count + 1);
        setAmount(amount + price);
        let cart = JSON.parse(localStorage.getItem('cart'));

        if(cart){
            let itemId = cart.find(item => item.id === id);
            if(itemId){
                cart = cart.map(item => {
                    if(item.id === id){
                        return {
                            ...item,
                            quantity:item.quantity + count
                        }
                    }
                })
            }
        }
        else{
            cart=[{
                id:id,
                count:count + 1,
                amount:amount + price
            }];
            localStorage.setItem('cart',JSON.stringify(cart))
            //console.log(cart);
            console.log(localStorage.cart[0].count);
        }

    }

    return (
        <>
            <Navbar />
            <div className="home-cont" >
                <div className="filter-cont" >
                    <input
                        type="number"
                        className="filter-Input"
                        placeholder="Min"
                        value={min}
                        onChange={(e) => setMin(e.target.value)} />
                    <input
                        type="number"
                        className="filter-Input"
                        placeholder="Max"
                        value={max}
                        onChange={(e) => setMax(e.target.value)} />
                    <button onClick={handlePriceFilter} className="filter-btn" >Filter</button>
                </div>
                <div className="home-search" >
                    <label htmlFor="search" className="search-label" >
                        <SearchIcon style={{ fontSize: '28px' }} />
                    </label>
                    <input
                        type="text"
                        id='search'
                        placeholder="Search..."
                        onChange={(e) => setSearchItem(e.target.value)} />
                    <button className="search-btn" onClick={handleSearch} >Search</button>
                </div>
                <div>
                    Cart
                    {/* <p>{cart.count} & {cart.amount}</p> */}
                </div>
            </div>
            <div className="home-card" >
                {
                    result.length ? result.map((item) => {
                        return <Card item={item} key={item.id} />
                    }) :
                        products.map((item) => {
                            return <Card item={item} key={item.id} handleCartCount={handleCartCount} />
                        })
                }
            </div>
        </>
    )
}
export default Home;
